(1008, 'wpmembers_dialogs', 'a:9:{s:14:\"restricted_msg\";s:119:\"This content is restricted to site members.  If you are an existing user, please log in.  New users may register below.\";s:4:\"user\";s:50:\"Sorry, that username is taken, please try another.\";s:5:\"email\";s:74:\"Sorry, that email address already has an account.<br />Please try another.\";s:7:\"success\";s:124:\"Congratulations! Your registration was successful.<br /><br />You may now log in using the password that was emailed to you.\";s:11:\"editsuccess\";s:29:\"Your information was updated!\";s:11:\"pwdchangerr\";s:53:\"Passwords did not match.<br /><br />Please try again.\";s:16:\"pwdchangesuccess\";s:30:\"Password successfully changed!\";s:11:\"pwdreseterr\";s:65:\"Either the username or email address do not exist in our records.\";s:15:\"pwdresetsuccess\";s:135:\"Password successfully reset!<br /><br />An email containing a new password has been sent to the email address on file for your account.\";}', 'yes'),
(1009, 'wpmembers_email_newreg', 'a:2:{s:4:\"subj\";s:37:\"Your registration info for [blogname]\";s:4:\"body\";s:256:\"Thank you for registering for [blogname]\n\nYour registration information is below.\nYou may wish to retain a copy for your records.\n\nusername: [username]\npassword: [password]\n\nYou may log in here:\n[reglink]\n\nYou may change your password here:\n[user-profile]\n\";}', 'no'),
(1010, 'wpmembers_email_newmod', 'a:2:{s:4:\"subj\";s:40:\"Thank you for registering for [blogname]\";s:4:\"body\";s:170:\"Thank you for registering for [blogname]. \nYour registration has been received and is pending approval.\nYou will receive login instructions upon approval of your account\n\";}', 'no'),
(1011, 'wpmembers_email_appmod', 'a:2:{s:4:\"subj\";s:50:\"Your registration for [blogname] has been approved\";s:4:\"body\";s:287:\"Your registration for [blogname] has been approved.\n\nYour registration information is below.\nYou may wish to retain a copy for your records.\n\nusername: [username]\npassword: [password]\n\nYou may log in and change your password here:\n[user-profile]\n\nYou originally registered at:\n[reglink]\n\";}', 'no'),
(1012, 'wpmembers_email_repass', 'a:2:{s:4:\"subj\";s:34:\"Your password reset for [blogname]\";s:4:\"body\";s:152:\"Your password for [blogname] has been reset\n\nYour new password is included below. You may wish to retain a copy for your records.\n\npassword: [password]\n\";}', 'no'),
(1013, 'wpmembers_email_notify', 'a:2:{s:4:\"subj\";s:36:\"New user registration for [blogname]\";s:4:\"body\";s:182:\"The following user registered for [blogname]:\n\nusername: [username]\nemail: [email]\n\n[fields]\nThis user registered here:\n[reglink]\n\nuser IP: [user-ip]\n\nactivate user: [activate-user]\n\";}', 'no'),
(1014, 'wpmembers_email_footer', '----------------------------------\nThis is an automated message from [blogname]\nPlease do not reply to this address', 'no'),
(1015, 'wpmembers_email_getuser', 'a:2:{s:4:\"subj\";s:23:\"Username for [blogname]\";s:4:\"body\";s:61:\"Your username for [blogname] is below.\n\nusername: [username]\n\";}', 'no'),
